// SearchForm.js
import React from 'react';
import styles from './navbarSearch.module.css'; // Import your CSS module

const clearInput = () => {
    const input = document.getElementsByTagName("input")[0];
    input.value = "";
  }

  const clearBtn = document.getElementById("clear-btn");
  clearBtn.addEventListener("click", clearInput);

const navbarSearch = () => {
  return (
    <form className={styles.searchForm}>
      <input type="search" required className={styles.input} />
      <i className={`fa fa-search ${styles.fa}`} />
      <a href="javascript:void(0)" id="clear-btn" className={styles.clearBtn}>
        Clear
      </a>
    </form>
  );
};

export default navbarSearch;
